  //gives strings after removing all emojis from a string
  function removeNonPrintableChars($string){
    $string = preg_replace('/[\x00-\x1F\x7F-\xFF]/', '', $string);
    $string = preg_replace('/[\x00-\x1F\x7F]/', '', $string);
    $string = preg_replace('/[\x00-\x1F\x7F]/u', '', $string);
    $string = preg_replace('/[\x00-\x1F\x7F\xA0]/u', '', $string);
    return $string;
  }  
  
  //trims left, right only spaces completely! note, wont trims \r\n
  //replaces multiple spaces into single space
  //removes all \r\n if $should_remove_new_lines_also flag is "true"
  //e.g. myTrim("Ravi   Tewari\r\nHello", true);
  //e.g. myTrim("Ravi   Tewari\r\nHello", false);
  function myTrim($string, $should_remove_new_lines_also) {
    $string = trim($string, " ");   //trims " " not \r\n
    $string = preg_replace("/[[:blank:]]+/"," ", $string);
    return $should_remove_new_lines_also ? preg_replace('!\s+!', ' ', $string) : $string;
  }
  
  //checks if $string contains all keywords as supplied in $keywords_array
  //$ignore_case decides whether we need case sensitive check
  function stringContainsAll($string, $keywords_array, $ignore_case = true) {
    foreach($keywords_array as $keyword) {
      $pattern = $ignore_case ? "/$keyword/i" : "/$keyword/"; 
      if(!preg_match($pattern, $string)) return false;
    }
    return true;
  }
  
  //checks if $string contains any of the keywords as supplied in $keywords_array
  //$ignore_case decides whether we need case sensitive check
  function stringContainsAny($string, $keywords_array, $ignore_case = true) {
    foreach($keywords_array as $keyword) {
      $pattern = $ignore_case ? "/$keyword/i" : "/$keyword/"; 
      if(preg_match($pattern, $string)) return true;
    }
    return false;
  }
  
  //Use: Take backup of file before modifying
  function takeBackup($_fullFileNameToTakeBackup) {
    $bkp_dir = "{$_SERVER['DOCUMENT_ROOT']}/000-Ravi-DontDelete/bkp";
    $ok_to_go = true;
    if(!is_dir($bkp_dir)) {
      $ok_to_go = false;
      if(mkdir($bkp_dir)) {
        $ok_to_go = true;
      }
    }
    if($ok_to_go) {
      $origFileNameOnly = pathinfo($_fullFileNameToTakeBackup, PATHINFO_BASENAME);
      $bkpFileNameOnly = pathinfo($_SERVER['SCRIPT_NAME'], PATHINFO_BASENAME) . ".$origFileNameOnly";
      copy($_fullFileNameToTakeBackup, "$bkp_dir/$bkpFileNameOnly");
    }
  }

  //$input_date_fmt MUST match the format of $input_date. Otherwise you get wrong output!
  //Will return $desired_format date
  //e.g. ('10.04.20', 'd.m.y', 'Y/m/d') => will return 2020/04/10
  function getAnyFormatDate($input_date, $input_date_fmt, $desired_format) {
    $date_obj = datetime::createfromformat($input_date_fmt, $input_date);
    return $date_obj->format($desired_format);    
  }
  
  //NOTE: $string_date must be in $date_fmt format. otherwise you won't get expected results
  //e.g. echo addDaysToDate(1, '27.02.20', 'd.m.y', 'd.m');
  function addDaysToDate($days_to_add, $string_date, $date_fmt, $date_return_fmt="") {   
    $date_return_fmt = empty($date_return_fmt) ? $date_fmt : $date_return_fmt;
    $date_obj = datetime::createfromformat($date_fmt, $string_date);
    date_add($date_obj, date_interval_create_from_date_string("$days_to_add days"));
    return date_format($date_obj, $date_return_fmt);
  }

  //finds all the files using $files_filter filter inside folder/subfolder starting from $start_dir
  //it goes upto $depth level to search for the file
  //and stores the output in array $output_array
  //NOTE: &$output_array is called by reference
  //  ALSO, in this function, it won't give the depth of the file
  //e.g. allFilesWithoutDepth("/var/www/vhosts/babamurli.com/BABAMURLI/01. Daily Murli", "*.pdf", 5, $output_array);
  //e.g. allFilesWithoutDepth("/var/www/vhosts/babamurli.com/BABAMURLI/01. Daily Murli", "*01.04.20*", 5, $output_array);
  function allFilesWithoutDepth($start_dir, $files_filter, $depth, &$output_array) {
    $all_files = array();
    $cmd = "find \"$start_dir\" -maxdepth $depth -name \"$files_filter\" -type f | sort";
    exec($cmd, $output_array);
  }
  
  //finds all the files using $files_filter filter inside folder/subfolder starting from $start_dir
  //it goes upto $depth level to search for the file
  //and stores the output in array $output_array
  //NOTE: &$output_array is called by reference
  //  The output array is an assiciative array having full file name as index key and depth (w.r.t $start_dir) as value
  //  e.g. $output_array["..../01. Daily Murli/.../abc.htm"] = 4
  //e.g. allFilesWithDepth("/var/www/vhosts/babamurli.com/BABAMURLI/01. Daily Murli", "*.pdf", 5, $output_array);
  //e.g. allFilesWithDepth("/var/www/vhosts/babamurli.com/BABAMURLI/01. Daily Murli", "*01.04.20*", 5, $output_array);
  function allFilesWithDepth($start_dir, $files_filter, $depth, &$output_array) {
    $all_files = array();
    //NOTE: %p-> full file path, %d-> depth of file, %h-> file without leading dirs, %f-> only filename without leading dirs
    $cmd = "find \"$start_dir\" -maxdepth $depth -name \"$files_filter\" -type f -printf '%pRAV_SEP%d\n' | sort";
    exec($cmd, $output_array);   //output will look like: [....fileRAV_SEP3]
    foreach($output_array as $output) {
      $tmp = explode("RAV_SEP", $output);   //e.g. [....fileRAV_SEP3]
      $tmp_output_array[$tmp[0]] = intval($tmp[1]);
    }
    $output_array = $tmp_output_array;
  }
  
  //finds all the dirs using $dirs_filter filter inside folder/subfolder starting from $start_dir
  //it goes upto $depth level to search for the dir
  //and stores the output in array $output_array
  //NOTE: &$output_array is called by reference
  //  The output array is an assiciative array having full dir name as index key and depth (w.r.t $start_dir) as value
  //  e.g. $output_array["..../01. Daily Murli/.../MyDir"] = 4
  //e.g. allDirsWithDepth("/var/www/vhosts/babamurli.com/BABAMURLI/01. Daily Murli", "*", 5, $output_array);
  //e.g. allDirsWithDepth("/var/www/vhosts/babamurli.com/BABAMURLI/01. Daily Murli", "*MP3*", 5, $output_array);
  function allDirsWithDepth($start_dir, $dirs_filter, $depth, &$output_array) {
    $all_files = array();
    //NOTE: %p-> full file path, %d-> depth of file, %h-> file without leading dirs, %f-> only filename without leading dirs
    $cmd = "find \"$start_dir\" -maxdepth $depth -name \"$dirs_filter\" -type d -printf '%pRAV_SEP%d\n' | sort";
    exec($cmd, $output_array);   //output will look like: [....fileRAV_SEP3]
    foreach($output_array as $output) {
      $tmp = explode("RAV_SEP", $output);   //e.g. [....dirRAV_SEP5]
      $tmp_output_array[$tmp[0]] = intval($tmp[1]);
    }
    $output_array = $tmp_output_array;
  }
  
  //finds all the directories using $dir_filter filter inside folder/subfolder starting from $start_dir
  //it goes upto $depth level to search for the directories
  //and stores the output in array $output_array
  //NOTE: &$output_array is called by reference
  //  ALSO, in this function, it won't give the depth of the dir
  //e.g. allDirsWithoutDepth("/var/www/vhosts/babamurli.com/BABAMURLI/01. Daily Murli", "*", 5, $output_array);
  //e.g. allDirsWithoutDepth("/var/www/vhosts/babamurli.com/BABAMURLI/01. Daily Murli", "*MP3*", 5, $output_array);
  function allDirsWithoutDepth($start_dir, $dir_filter, $depth, &$output_array) {
    $all_files = array();
    $cmd = "find \"$start_dir\" -maxdepth $depth -name \"$dir_filter\" -type d | sort";
    exec($cmd, $output_array);
  }
  
  //Returns an array of all the files inside a directory. Files "." & ".." are excluded
  //$sortOnName_or_sortOnTime: 0=>sort on name, 1=>sort on filetime
  //$asc_or_dsc: 0=>ascending sort, 1=>descending sort
  //e.g. myScan("/tmp/abc", "*.pdf", 1, 1)
  function myScan($dir, $file_filter, $asc_or_dsc = 1, $sortOnName_or_sortOnTime = 0) {
    $files = array();
    $array_of_files = glob("$dir/$file_filter");
    if(!$array_of_files) return $files;   //if no match for the filter, return blank array
    foreach($array_of_files as $file) {
      $files[pathinfo($file, PATHINFO_BASENAME)] = filemtime($file);
    }
    if($asc_or_dsc === 0) {   //asc sort
      if($sortOnName_or_sortOnTime === 0) {   //sort on filename
        ksort($files);
      } else if($sortOnName_or_sortOnTime === 1) {   //sort on filetime
        asort($files);
      }
    } else if($asc_or_dsc === 1) {   //desc sort
      if($sortOnName_or_sortOnTime === 0) {   //sort on filename
        krsort($files);
      } else if($sortOnName_or_sortOnTime === 1) {   //sort on filetime
        arsort($files);
      }
    }
    $files = array_keys($files);
    return ($files) ? $files : array();
  }
  
  //Returns an array of all the files inside a directory. Files "." & ".." are excluded
  function dirToArray($dir) {
    $result = array();
    $cdir = scandir($dir);
    foreach($cdir as $key => $value) {
      if(!in_array($value, array(".",".."))) {
        $result[] = $value;
      }
    }
    return $result;
  }
  
  //It deletes all the files from inside a directory
  function emptyThisDirectory($dir) {
    $array_of_files = glob("$dir/*");
    if(!$array_of_files) return false;   //no match found for the filter
    if($array_of_files !== false) {
      foreach($array_of_files as $f) {
        unlink($f);
      }
    }
  }

